import { createStore } from 'vuex';

export default createStore({
  state: {
    boxes: ['Products', 'Brands', 'Lists ', 'Campaigns', 'Creators', 'Support', 'Settings', 'Reports'],
    selectedBox: null,
  },
  mutations: { 
    selectBox(state, index) {
      state.selectedBox = state.boxes[index];
    }
  },

  actions: {
    // your actions
  },
  modules: {
    // your modules
  }
});
